function isMobile () {
	if (window.innerWidth <= 640) {
		return true;
	} else {
		return false;
	}
}

function isTablet () {
	if (window.innerWidth > 640 && window.innerWidth <= 1024) {
		return true;
	} else {
		return false;
	}
}

function isDesktop () {
	if (window.innerWidth > 1024) {
		return true;
	} else {
		return false;
	}
}

$(document).ready(function(){

function adaptive () {
	if (isMobile()) {
		if ($('#filterpro_box')) {
	        $('#filterpro_box').clone(true).appendTo('.product-filter');
	        $('#filterpro_box').first().remove();
	        $('#filterpro_box').attr('class', '');
	    }
	    if (typeof display == 'function') {
	    	display('list');
	    }
	}
	if (isTablet()) {
		if ($('#filterpro_box')) {
	        $('#filterpro_box').clone(true).appendTo('.product-filter');
	        $('#filterpro_box').first().remove();
	        $('#filterpro_box').attr('class', '');
	    }
	    if (typeof display == 'function') {
	    	display('grid');
	    }
	}
	if (isDesktop()) {
		if ($('#filterpro_box').parent().get(0) && $('#filterpro_box').parent().get(0).id !== "column-left") {
			$('#filterpro_box').clone(true).appendTo('#column-left');
			$('.product-filter #filterpro_box').remove();
			$('#filterpro_box').attr('class', 'box');
		}
		$('#filterpro_box .box-content').attr('style', '');
		$('#menu').attr('style', '');
		var h = ($('#menu > ul > li > a.active + div').height())+20;
        if( h > 0 ){
            $('#column-left').css({
                'padding-top': h+'px'
            });
        };
		if (typeof display == 'function') {
			display('grid');
		}
	}
}

adaptive();

$(window).resize(adaptive);

});