function gUV(e, f)
{
	var c = String(e).split("?");
	var a = "";
	if(c[1])
	{
		var b = c[1].split("&");
		for(var g = 0; g <= (b.length); g++)
		{
			if(b[g])
			{
				var d = b[g].split("=");
			
				if(d[0] && d[0] == f)
				{
					a = d[1];
				}
			}
		}
	}
	return a;
}

